            }
        }
    }
]

# Processing the data to extract the required information
classified_data_types = defaultdict(list)
cleaned_use_cases = []

for entry in data:
    classification = entry["classification"]
    data_types = entry["data_types"]
    use_case_1 = entry["use_case_1"]["example"]

    # Collecting classified data types
    classified_data_types["String"].extend(use_case_1["source"])
    classified_data_types["Numerical"].extend(use_case_1["target"])

    # Collecting use cases
    cleaned_use_cases.append({
        "description": use_case_1["description"],
        "solution": use_case_1["solution"],
        "source": use_case_1["source"],
        "target": use_case_1["target"],
        "conditions": use_case_1["conditions"],
        "value": use_case_1["value"]
    })

# Converting the dictionaries to lists
classified_data_types_list = [[value for key in classified_data_types] for key in classified_data_types.keys()]

# Output format as per the requirement
output = {
    "classification": list(classified_data_types_list[0]),
    "data_types": list(classified_data_types_list[1]),
    "use_case_1": cleaned_use_cases
}

# Print the processed data to verify
print(json.dumps(output, indent=4))
