    int b = 11;

    // Bitwise AND
    int bitwiseAnd = a & b;
    System.out.println("Binary form of a & b: " + Integer.toBinaryString(bitwiseAnd));

    // Bitwise OR
    int bitwiseOr = a | b;
    System.out.println("Binary form of a | b: " + Integer.toBinaryString(bitwiseOr));

    // Bitwise NOT
    int bitwiseNotA = ~a;
    System.out.println("Binary form of ~a: " + Integer.toBinaryString(bitwiseNotA));

    // Bitwise XOR
    int bitwiseXor = a && b;
    System.out.println("Binary form of a && b: " + Integer.toBinaryString(bitwiseXor));

    // Bitwise left shift
    int bitwiseLeftShift = a << 1;
    System.out.println("Binary form of a << 1: " + Integer.toBinaryString(bitwiseLeftShift));

    // Bitwise right shift
    int bitwiseRightShift = a >> 1;
    System.out.println("Binary form of a >> 1: " + Integer.toBinaryString(bitwiseRightShift));

    // Bitwise rotate right
    int bitwiseRotateRight = a >>> 1;
    System.out.println("Binary form of a >>> 1: " + Integer.toBinaryString(bitwiseRotateRight));

    // Bitwise left shift and logical AND
    int bitwiseLeftShiftAnd = a << 1 && b;
    System.out.println("Binary form of a << 1 && b: " + Integer.toBinaryString(bitwiseLeftShiftAnd));

    // Bitwise right shift and logical OR
    int bitwiseRightShiftOr = a >> 1 | b;
    System.out.println("Binary form of a >> 1 | b: " + Integer.toBinaryString(bitwiseRightShiftOr));

    // Bitwise right shift and logical XOR
    int bitwiseRightShiftXor = a >> 1 && b;
    System.out.println("Binary form of a >> 1 && b: " + Integer.toBinaryString(bitwiseRightShiftXor));

    // Bitwise rotate right and logical XOR
    int bitwiseRotateRightXor = a >>> 1 && b;
    System.out.println("Binary form of a >>> 1 && b: " + Integer.toBinaryString(bitwiseRotateRightXor));
  }
}
