### Javascript Implementation

```javascript
// Function for the searching the starting index of the subarray
function search(searchspace, s, e, key) {
    let ans = -1;
    // Binary search
    while (s <= e) {
        let mid = Math.floor((s + e) / 2);
        if (searchspace[mid] - key >= 0) {
            ans = mid;
            e = mid - 1;
        } else {
            s = mid + 1;
        }
    }
    return ans;
}

// Function to return the longest subarray
function longestSubarray(a, n) {
    let SuffixSum = new Array(n + 1).fill(0);
    SuffixSum[n] = 0;
    for (let i = n - 1; i >= 0; --i) {
        SuffixSum[i] = SuffixSum[i + 1] + a[i];
    }
    let ans = 0;
    let searchspace = new Array(n).fill(0);
    let index = new Array(n).fill(0);
    let j = 0;
    for (let i = 0; i < n; ++i) {
        if (j == 0 || SuffixSum[i] > searchspace[j - 1]) {
            searchspace[j] = SuffixSum[i];
            index[j] = i;
            j++;
        }
        let idx = search(searchspace, 0, j - 1, SuffixSum[i + 1]);
        if (idx != -1)
            ans = Math.max(ans, i - index[idx] + 1);
    }
    return ans;
}

// Driver Code
let a = [ -1, 4, -2, -5, 6, -8 ];
let n = a.length;
console.log(longestSubarray(a, n));
