import java.util.PriorityQueue;
import java.util.Comparator;

public class FibonacciHeap {

    static class Node {
        int weight;
        int degree;
        boolean mark;
        Node parent;
        Node sibling;
        Node child;

        // For the Fibonacci heap data structure
        Node previous;
        Node next;
    }

    static class Entry {
        int key;
        Node node;
    }

    private PriorityQueue<Node> rootList;
    private int minNodeKey;
    private Comparator<Entry> comparator;

    public FibonacciHeap() {
        rootList = new PriorityQueue<>(Comparator.comparing(Entry::getKey));
        minNodeKey = Integer.MAX_VALUE;
        comparator = new Comparator<Entry>() {
            public int compare(Entry o1, Entry o2) {
                return Integer.compare(o1.getKey(), o2.getKey());
            }
        };
    }

    public int getWeight(int key) {
        Node node = search(key);
        if (node != null) return node.weight;
        else return -1;
    }

    public int getDegree(int key) {
        Node node = search(key);
        if (node != null) return node.degree;
        else return -1;
    }

    public int getParent(int key) {
        Node node = search(key);
        if (node != null) {
            Node parent = node.parent;
            if (parent != null) return parent.weight;
            else return -1;
        }
        else return -1;
    }

    public int getSibling(int key) {
        Node node = search(key);
        if (node != null) {
            Node sibling = node.sibling;
            if (sibling != null) return sibling.weight;
            else return -1;
        }
        else return -1;
    }

    public int getFirstChild(int key) {
        Node node = search(key);
        if (node != null) {
            Node child = node.child;
            if (child != null) return child.weight;
            else return -1;
        }
        else return -1;
    }

    public int getChild(int key, int index) {
        Node node = search(key);
        if (node != null) {
            Node child = node.child;
            Node sibling = child;
            for (int i = 1; i < index && sibling != null; i++) {
                sibling = sibling.next;
                assert sibling != null; // Because the index is out of the root list
            }
            if (sibling != null) return sibling.weight;
            else return -1;
        }
        else return -1;
    }

    private Node search(int key) {
        for (Node n : rootList) if (n.node.key == key) return n.node;
        return null;
    }

    private void mergeRootNode(Node a, Node b) {
        a.prev = b;
        b.next = a;
    }

    private void cutChildNode(Node x, Node y) {
        Node pred = y;
        y = y.next;
        while (y != pred) {
            if (y == x) break;
            y = y.next;
        }
        y.prev = pred;
        pred.next = y;
        x.next = x.child;
        x.prev = x.child;
        x.child = null;
        x.degree = 0;
    }

    private void fibHeapConsolidate() {
        int maxDegree = 2 * log2(rootList.size()) + 4;
        boolean[] marked = new boolean[maxDegree];
        for (Entry entry : rootList) {
            while (entry.getKey() != minNodeKey) {
                Node node = entry.getNode();
                Node sibling = node.sibling;
                while (sibling != null) {
                    Node pred = sibling;
                    sibling = sibling.next;
                    cutChildNode(node, pred);
                    node = entry.getNode();
                    while (node != null) {
                        node.prev = node.next = node.child;
                        node.child = null;
                        node.degree = 0;
                        node.mark = false;
                        entry = new Entry(node.weight, node);
                    }
                    node = entry.getNode();
                    entry = new Entry(minNodeKey, node);
                }
                marked[node.degree] = true;
                node.degree = 0;
            }
        }
    }

    public void fibHeapDecreaseKey(int key, int newKey) {
        Node node = search(key);
        if (node.weight > newKey) {
            throw new IllegalArgumentException("New key is bigger than the current one");
        } else {
            node.weight = newKey;
            Node parent = node.parent;
            if (parent != null && node.weight < parent.weight) {
                cutChildNode(node, parent);
                if (parent.parent != null) mergeRootNode(parent.parent, parent);
                else fibHeapConsolidate();
            }
            if (newKey < minNodeKey) {
                minNodeKey = newKey;
            }
        }
    }

    private void fibHeapDelete(int key) {
        Node node = search(key);
        fibHeapDecreaseKey(node.weight, -1);
        Node x = node.parent;
        mergeRootNode(x, node);
        if (minNodeKey == -1) {
            minNodeKey = rootList.peek().getKey();
        }
        fibHeapConsolidate();
        while (rootList != null && rootList.peek().getKey() == -1) {
            rootList.remove();
        }
    }

    public int fibHeapExtractMin() {
        Node node = rootList.peek();
        int minKey = node.getKey();
        fibHeapDelete(minKey);
        return minKey;
    }

    public void fibHeapInsert(int key, int weight) {
        Node node = new Node();
        node.key = key;
        node.weight = weight;
        for (Node n : rootList) {
            mergeRootNode(n, node);
            rootList.update(node, node);
            break;
        }
        if (node.weight < minNodeKey) {
            minNodeKey = node.weight;
        }
    }
}
