#include <algorithm>
#include <iostream>
using namespace std;

const int N = 21;
int dp[N][N][N], visited[N][N][N];

int main() {
    // Initialize dp array
    fill(dp, dp + N * N * N, 0);

    // Initialize visited array
    fill(visited, visited + N * N * N, false);

    // Start with dp[1][1][1] = 1
    dp[1][1][1] = 1;

    // Iterate over all values of a, b, c and update dp array
    for (int a = 1; a <= N; a++) {
        for (int b = 1; b <= N; b++) {
            for (int c = 1; c <= N; c++) {
                if (a * a + b * b == c * c) {
                    // If a, b, c is a "quadratic triple"
                    // Update dp array by adding current value and the sum of all states formed from a, b
                    dp[a][b][c] += visited[a][b][c];
                    for (int i = 1; i <= a; i++) {
                        for (int j = 1; j <= b; j++) {
                            dp[i + a][j + b][c] += dp[i][j][c];
                        }
                    }
                }
            }
        }
    }

    // Print the final result
    cout << dp[N][N][N] << endl;

    return 0;
}
