def calculate_f(n):
    def sum_diff(s):
        subsets = subsets_with_sum_diff(s, 1)
        return len(subsets)

    def subsets_with_sum_diff(subset, target_diff):
        count = 0
        for i in range(1, len(subset)):
            for comb in combinations(subset, i):
                a_sum = sum(comb)
                b_sum = sum(set(subset) - set(comb))
                if abs(a_sum - b_sum) == target_diff:
                    count += 1
        return count

    s = list(range(1, n + 1))
    return sum_diff(s)

