public class Catalan {

    public int catalan(int n) {
        long C = 1;
        for (int i = 0; i < n; i++)
            C = C * (4 * i + 2) / (i + 2);
        return (int) (C / (n + 1));
    }

    public int solution(int n) {
        int[] a = new int[n + 1];
        for (int i = 0; i <= n; i++) {
            if (i == 0 || i == 1)
                a[i] = 1;
            else
                a[i] = a[i - 1] * (4 * i - 2) / i;
        }
        return a[n];
    }
}
